    }

    changeValue =  (e) =>{
        var rslt =e.target.value
        this.setState({searchValue : rslt})
        rslt = rslt.trim()        
        console.log(this.state.searchValue)

    }
    search = async (e)=>{
        e.preventDefault()
        const result = document.getElementById('result').innerHTML
        const APP_kEY = '30b680a61787c4eb4f211382ef68e0c9'
        const APP_ID ='6d7ac30d'
        const apiURL = `https://api.edamam.com/search?q=chicken&app_id=${APP_ID}&app_key=${APP_kEY}`
        const searchResult = await fetch(`${apiURL}`)
        const data = await searchResult.json();
        console.log(data)
        result =    <ul class="song-list">
        ${data.data
          .map(song=> `<li>
                      <div>
                          <strong>${song.artist.name}</strong> -${song.title} 
                      </div>
                      <span data-artist="${song.artist.name}" data-songtitle="${song.title}"> get lyrics</span>
                  </li>`
          )
          .join('')}
      </ul>

    }

    render() {
        return(
            <div className='form'>
                <label> Search Your Recipe</label>
                <form>
                    <input type='text' id='a' onChange={this.changeValue} value={this.state.searchValue} />
                    <button onClick={this.trya}> Search </button>    
                </form> 
               <div id='result'></div>

            </div>
        )
    };
}

ReactDOM.render(
    <App />,
    document.getElementById('root')
);
