import React from 'react'
import ReactDOM from 'react-dom'
// import style from './css/main.css'


class App extends React.Component {
  state = {
    name: ''
  };
  upName = (e) =>{
      this.setState({
        name: e.target.value
      })
  }
  render() {
    return (
      <div>
        <p>Updated name: {this.state.name}</p>
        <p>Change name:</p>
        <input
          type="text"
          onChange={this.upName}
        />
      </div>
    );
  }
}

ReactDOM.render(
    <App />,
    document.getElementById('root')
);
