import React from 'react'

class Image extends React.Component {
    showMoreInfo =() =>{
        document.getElementById('moreInfo').style.display='flex'
    }
    render(){
        return(
            <div className='image'>
                <img src={this.props.image} />
                <div className='empty'>

                </div>
                <div className='image-content' >
                    <div className='text'>
                        <h1>
                            {this.props.name}

                        </h1>
                        <p>Total Ingredients : {this.props.ingredients}</p>

                        <span onClick={this.showMoreInfo}> Know More &#8594;</span>
                    </div>
                </div>
                <div id='moreInfo'>
                    <div class='content'>
                        {
                        <img src={this.props.image} />

                        }
                    </div>
                </div>

            </div>
        )
    }
}
export default Image
