import React from 'react'
// import MoreInfo from './MoreInfo'

class Image extends React.Component {
    showMoreInfo =() =>{
        document.getElementById('moreInfo').style.display='flex'
    }
    render(){
        return(
            <div>
                <div className='image'>
                <img src={this.props.image} />
                <div className='empty'>

                </div>
                <div className='image-content' >
                    <div className='text'>
                        <h1>
                            {this.props.name}

                        </h1>
                        <p>Total Ingredients : {this.props.ingredients}</p>

                        <span onClick={this.showMoreInfo}> Know More &#8594;</span>
                    </div>
                </div>
            </div>
                {/* <MoreInfo 
                name = {this.props.label}
                image = {this.props.image}
                ing = {this.props.ingredients}
                ingList = {this.props.ingredientsLines}                
                /> */}
            </div>
        )
    }
}
export default Image
