import React from 'react'
// import MoreInfo from './MoreInfo'

class Image extends React.Component {
    showMoreInfo =() =>{
        document.getElementById('moreInfo').style.display='flex'
    }
    render(){
        return(
                <div className='image'>
                <img src={this.props.image} />
                <div className='empty'>

                </div>
                <div className="steps">
                    {/* {this.props.ingredientLines.map((line)=>(
                        <ul>
                            <li>
                                {line}
                            </li>
                        </ul>
                    ))} */}
                </div>
                <div className='image-content' >
                    <div className='text'>
                        <h1>
                            {this.props.name}
                            {console.log(this.props)}

                        </h1>
                        <p>Total Ingredients : {this.props.ingredients}</p>

                        <span onClick={this.showMoreInfo}> Know To Make It &#8594;</span>
                    </div>
                </div>
            </div>

        )
    }
}
export default Image
